package p3;

public class Pyramids {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for (int i=1; i <=5; i++) {
	        for(int j=1; j <= 5; j++)
	        if(j<=i)
	        System.out.print(""); // space
	        else
	        System.out.print(" *"); // star
	        // new line
	        System.out.println();

	}

}
	
}
